,p_plsql_code=>wwv_flow_string.join(wwv_flow_t_varchar2(
'-- GLOBAL',
'subtype gt_string is varchar2(32767);',
'',
'gco_min_lov_cols constant number := 2;',
'gco_max_lov_cols constant number := 3;',
'',
'gco_lov_display_col constant number := 1;',
