'--Anne Richter, 29.11.2017',
'--Zuordnung Werk Band Modell',
'with wkb as (',
'  /* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++',
'    View - Darstellung der zum Projekt zugeordneten Werke/Bänder und Modelle',
'',
'    Autor: Anne Richter',
'    Datum: 09.01.2018',
'',
'    Datum       Autor         Beschreibung',
'    --------------------------------------------------------------------------------',
'    09.01.2018  Anne Richter  Neuerstellung',
'',
'  ++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */',
'select     ',
'    --Werksinformation',
'    tdp.pk_db_proj,',
'    tdpvwb.FK_DB_STD_VERB_WK_BD,',
'    tdpvwb.pk_DB_PROJ_VERB_WK_BD,',
'    tdsw.werk_name,',
'    tdsw.werk_nr_long,',
'    tdsw.werk_nr_short,',
'    tdswb.BAND_NAME,',
'    tdpvwbl.FK_DB_LOGTEIL,',
'    tdpvwbml.pk_DB_PROJ_VERB_WK_BD_MOD_LOG',
'from T_db_proj tdp ',
' join T_DB_PROJ_VERB_WK_BD tdpvwb on tdpvwb.FK_DB_PROJ  = tdp.pK_DB_PROJ',
' join T_DB_STD_WK_BD tdswb on tdswb.pk_db_std_wk_bd = tdpvwb.FK_DB_STD_VERB_WK_BD',
' join t_db_std_wk tdsw on tdsw.pk_db_std_wk = tdswb.fk_db_std_wk',
' join T_DB_PROJ_VERB_WK_BD_LOG tdpvwbl on tdpvwbl.FK_DB_PROJ_VERB_WK_BD  = tdpvwb.pK_DB_PROJ_VERB_WK_BD',
' left join T_DB_PROJ_VERB_WK_BD_MOD_LOG tdpvwbml on tdpvwbml.FK_DB_PROJ_VERB_WK_BD_LOG =  tdpvwbl.pK_DB_PROJ_VERB_WK_BD_LOG ',
'),',
'md as (',
'select ',
'    --Modellinformation',
'    tdp.pk_db_proj,',
'    tdsm.pk_db_std_mod,',
'    tdsm.modell_name,',
'    tdsm.produkt_id,',
'    tdpvwbml.pk_DB_PROJ_VERB_WK_BD_MOD_LOG',
