'    wkb.BAND_NAME,',
'    wkb.FK_DB_LOGTEIL,',
'    wkb.pk_DB_PROJ_VERB_WK_BD,',
'    --aus Modellabfrage',
'    --nvl(wkb.pk_DB_PROJ_VERB_WK_BD_MOD_LOG, md.pk_DB_PROJ_VERB_WK_BD_MOD_LOG)  pk_DB_PROJ_VERB_WK_BD_MOD_LOG,',
