'',
'                  if (pData.STATUS == 1) {',
'',
'                      apex.submit(''SPLIT'');',
'',
'                  } else {',
'',
'                      alert(''Unbekannter Fehler: Bitte versuchen Sie es zu einem späteren Zeitpunkt noch einmal oder wenden Sie sich an den Administrator der Software.'');',
'',
'                  }',
'',
'                }',
'            });',
'',
'        } else {',
'            alert(''Bitte wählen Sie mindestens 1 Teil aus, welches Sie splitten möchten.'');',
'        }   ',
'        ',
'    }',
'    ',
'    var ebr_gesamt = 0;',
'    var viewData = {};',
'    var spaltenCount = 0;',
'    var ebr_teil = 0;',
'',
'    $(document).ready(function() {',
'      lockBuchstaben();',
'    });',
'',
'    /*',
'        description: Loop over text elements from region REGION_VARIANZ.',
'                     fill variable viewData with objects and values',
'        author: Mario Wolf',
'        date: 24.08.2016',
'    */',
'    function fillDataView(){',
'        $( "#REGION_VARIANZ input:text[name^=''f'']" ).each(function(){',
'',
'            if(!(this.name in viewData)){',
'                viewData[this.name] = [];',
'            }',
'',
'            var tmpObj = {}; ',
'            tmpObj[''value''] = this.value;',
'            tmpObj[''html-id''] = this.id;',
'            tmpObj[''data-model-id''] = $(this).attr("data-model-id");',
'            tmpObj[''data-planung-id''] = $(this).attr("data-planung-id");',
'            tmpObj[''data-pid''] = $(this).attr("data-pid");',
'            tmpObj[''data-sub-pids''] = $(this).attr("data-sub-pids");',
'',
'            viewData[this.name][Object.keys(viewData[this.name]).length] = tmpObj;',
'',
'        });',
'    }',
'    ',
'    ',
'    /*',
'        description: fill dateView with text element values and ids',
'                     calculate ebr and fill it',
'        author: Mario Wolf',
'        date: 24.08.2016',
'    */',
'    function calculateDown() {',
'',
'        if( $.isNumeric( String($("#P146_EBR_GESAMT").val()).replace('','',''.'')) == false){',
'          alert(''Bitte geben Sie einen nummerischen Wert an!'');',
'          return;',
'        }',
'',
'        ebr_gesamt = parseFloat($("#P146_EBR_GESAMT").val().replace('','',''.''));',
'',
'        if(jQuery.isEmptyObject(viewData)){',
'            fillDataView();',
'        }',
'',
'',
'        $.each( viewData, function( key, elms ) {',
'          spaltenCount = Object.keys(viewData[key]).length;',
'',
'          if(spaltenCount <= 0 || isNaN(parseFloat(spaltenCount))) {',
'              ebr_teil = 0;',
'          }',
'          else {',
'            ebr_teil = parseFloat(ebr_gesamt) / parseFloat(spaltenCount);',
'          }	',
'',
'',
'            console.log (ebr_teil);',
'            ',
'            $.each( elms, function( key, obj ) {',
'                $(obj).attr("value", ebr_teil );',
'                $(''#'' + $(obj).attr("html-id")).val(ebr_teil.toFixed(2).toLocaleString().replace(''.'','',''));',
'            });',
'',
'        });',
'',
'    }',
'',
'    /* ',
'        description: Berechnet den Wert EBR Gesamt Rückwärts',
'        author: Stefan Geberl',
'        date: 25.09.2016',
'    */',
'    function calculateUp() {',
'',
'      var ebr_model = 0;',
'      var vol_anteil = 0;',
'      var vol_anteil_gesamt = 0;',
'      var calc_ebr_gesamt = 0;',
'',
'      viewData = {};',
'      fillDataView();',
'  ',
'      /*console.log(viewData);*/',
'        ',
'      $.each( viewData, function( key, elms ) {',
'',
'        ebr_model = 0;',
'',
'        $.each( elms, function( key, obj ) {',
'',
'          ebr_model += parseInt($(obj).attr("value"));      ',
'          ',
'          vol_anteil = $(''#PID_'' + $(obj).attr("data-model-id")).html()',
'          vol_anteil = vol_anteil.replace(''('','''').replace('')'','''').replace(''%'','''').replace('' '','''');',
'            ',
'          console.log( ''#PID_'' + $(obj).attr("data-model-id") );',
'            ',
'        });',
'',
'        console.log(''Summe: '' + ebr_model);',
'',
'        console.log(''Vol-Anteil: '' + vol_anteil );',
'',
'        vol_anteil_gesamt = vol_anteil_gesamt + parseInt(vol_anteil);',
'',
'        /*Einbaurate gesamt = Volumenanteil * EBR-Modell */',
'',
'        calc_ebr_gesamt = calc_ebr_gesamt + (ebr_model / 100 * vol_anteil)',
'',
'',
'      });',
'',
'      console.log(''Vol-Anteil (Gesamt): '' + vol_anteil_gesamt );',
'      console.log(''EBR (Gesamt): '' + calc_ebr_gesamt );',
'',
'      $(''#P146_EBR_GESAMT'').val( String(calc_ebr_gesamt).replace(''.'','','') );',
'',
'      calc_ebr_gesamt = 0;',
'        ',
'    }',
'       ',
'    ',
'    /* ',
'        description: Sperrt die Eingabe-Felder im Bericht f+r die Eingabe von Buchstaben. Es werden nur Zahlenwerte erlaubt.',
'        author: Stefan Geberl',
'        date: 25.09.2016',
'    */',
'    function lockBuchstaben() {',
'  ',
'    $( "#REGION_VARIANZ input:text[name^=''f'']" ).each(function(){',
'        $(''#''+ this.id).keydown(lock_key);',
'    });',
'',
'        $(''#P146_EBR_GESAMT'').keydown(lock_key);',
'    }',
'    ',
'    ',
'    function lock_key(e) {',
'        ',
'         console.log (e.keyCode); ',
'',
'            if ($.inArray(e.keyCode, [46, 8, 9, 27, 13, 110, 188]) !== -1 ||',
'                // Allow: Ctrl+A',
'                (e.keyCode == 65 && e.ctrlKey === true) ||',
'                // Allow: Ctrl+C',
'                (e.keyCode == 67 && e.ctrlKey === true) ||',
'                // Allow: Ctrl+X',
'                (e.keyCode == 88 && e.ctrlKey === true) ||',
'                // Allow: Ctrl+V',
'                (e.keyCode == 86 && e.ctrlKey === true) ||',
'                // Allow: home, end, left, right',
'                (e.keyCode >= 35 && e.keyCode <= 39)) {',
'                // let it happen, don''t do anything',
'                return;',
'            }',
'',
'            // Ensure that it is a number and stop the keypress',
'            if ((e.shiftKey || (e.keyCode < 48 || e.keyCode > 57)) && (e.keyCode < 96 || e.keyCode > 105)) {',
'                e.preventDefault();',
'            }',
'        ',
'    }',
'    ',
'    ',
'    ',
'    // Speichern-Prozess',
'    function save(p_close) {',
'',
'        var arr_ebr = [];',
'        var arr_teile_id = [];',
'        var arr_modell_id = [];',
'        var ajax_result;',
'        ',
'        viewData = {};',
'        fillDataView();',
'',
'        $.each( viewData, function( key, elms ) {',
'            $.each( elms, function( key, obj ) {',
'',
